---------------------------- Create table ----------------------------------
-- Customers
CREATE TABLE Customers (
    CustomerID INT PRIMARY KEY,
    FullName NVARCHAR(100),
    PhoneNumber NVARCHAR(20),
    Email NVARCHAR(100),
    CreatedAt DATETIME DEFAULT GETDATE()
);

-- Employees
CREATE TABLE Employees (
    EmployeeID INT PRIMARY KEY,
    FullName NVARCHAR(100),
    Role NVARCHAR(50), -- e.g., Waiter, Chef, Manager, Cashier
    PhoneNumber NVARCHAR(20),
    HireDate DATE
);

-- Tables (restaurant seating tables)
CREATE TABLE RestaurantTables (
    TableID INT PRIMARY KEY,
    TableNumber INT UNIQUE,
    Capacity INT,
    Status NVARCHAR(20) -- e.g., Available, Occupied, Reserved
);

-- Menu Items
CREATE TABLE MenuItems (
    MenuItemID INT PRIMARY KEY,
    Name NVARCHAR(100) not null unique,
    Description NVARCHAR(255),
    Price DECIMAL(10, 2),
    Category NVARCHAR(50) -- e.g., Appetizer, Main Course, Dessert
);

-- Orders
CREATE TABLE Orders (
    OrderID INT PRIMARY KEY,
    CustomerID INT,
    TableID INT,
    EmployeeID INT,
    OrderDate DATETIME DEFAULT GETDATE(),
    Status NVARCHAR(50), -- e.g., Pending, Completed, Cancelled
    FOREIGN KEY (CustomerID) REFERENCES Customers(CustomerID),
    FOREIGN KEY (TableID) REFERENCES RestaurantTables(TableID),
    FOREIGN KEY (EmployeeID) REFERENCES Employees(EmployeeID)
);

-- Order Details
CREATE TABLE OrderDetails (
    OrderDetailID INT PRIMARY KEY,
    OrderID INT,
    MenuItemID INT,
    Quantity INT,
    Price DECIMAL(10,2),
    FOREIGN KEY (OrderID) REFERENCES Orders(OrderID),
    FOREIGN KEY (MenuItemID) REFERENCES MenuItems(MenuItemID),
	unique(OrderID, MenuItemID)
);

-- Reservations
CREATE TABLE Reservations (
    ReservationID INT PRIMARY KEY,
    CustomerID INT,
    TableID INT,
    ReservationTime DATETIME,
    NumberOfPeople INT,
    Status NVARCHAR(50), -- e.g., Confirmed, Cancelled, No-show
    FOREIGN KEY (CustomerID) REFERENCES Customers(CustomerID),
    FOREIGN KEY (TableID) REFERENCES RestaurantTables(TableID)
);

